import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CartContext } from "../contexts/CartContext";
import { Card, CardMedia, CardContent, Typography, Button, Container } from "@mui/material";

interface Product {
  id: number;
  title: string;
  price: number;
  image: string;
  description: string;
}

const ProductDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const cartContext = useContext(CartContext);

  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) => setProduct(data));
  }, [id]);

  if (!product) return <Typography>Loading...</Typography>;

  return (
    <Container sx={{ marginTop: 4 }}>
      <Card sx={{ display: "flex", alignItems: "center", padding: 2 }}>
        <CardMedia component="img" sx={{ width: 200, height: 200 }} image={product.image} alt={product.title} />
        <CardContent>
          <Typography variant="h5">{product.title}</Typography>
          <Typography variant="body1">{product.description}</Typography>
          <Typography variant="h6">${product.price}</Typography>
          <Button variant="contained" color="primary" onClick={() => cartContext?.addToCart(product)}>
            Add to Cart
          </Button>
        </CardContent>
      </Card>
    </Container>
  );
};

export default ProductDetails;
